Date.prototype.format = function(formatString) {
	var formatMapper = {
		"%Y" : this.getFullYear(),
		"%m" : this.getMonth() + 1,
		"%d" : this.getDate(),
		"%H" : this.getHours(),
		"%M" : this.getMinutes(),
		"%s" : this.getSeconds(),
		"%S" : this.getMilliseconds(),
		"%w" : this.getDay()
	};
	if (formatString) {
		try {
			for ( var d in formatMapper) {
				var re = new RegExp(d, 'g');
				formatString = formatString
						.replace(
								re,
								(String(formatMapper[d]).length == 1 ? ("0" + formatMapper[d])
										: formatMapper[d]));
			}
		} catch (e) {
			consloe.log("FORMAT DATE ERROR:" + e);
		}
	}
	return formatString;
};
